import React, { useEffect, useState } from 'react';
import firebase from 'firebase';
import 'firebase/auth';

const UserList = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const database = firebase.database();
    const usersRef = database.ref('users');

    usersRef.on('value', (snapshot) => {
      const users = snapshot.val();
      const usersArray = Object.entries(users).map(([key, value]) => ({
        id: key,
        ...value,
      }));

      setUsers(usersArray);
    });

    const currentUserUid = firebase.auth().currentUser?.uid;
    if (currentUserUid) {
      const currentUserRef = database.ref(`users/${currentUserUid}`);
      currentUserRef.update({ online: true });

      currentUserRef.onDisconnect().update({ online: false });
    }

    return () => {
      if (currentUserUid) {
        const currentUserRef = database.ref(`users/${currentUserUid}`);
        currentUserRef.update({ online: false });
      }

      usersRef.off();
    };
  }, []);

  return (
    <div>
      <h2>User List</h2>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            {user.name} ({user.online ? 'online' : 'offline'})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserList;
