import {
  doc,
  setDoc,
  onSnapshot,
  collection,
  addDoc,
  query,
  orderBy,
  limit,
} from 'firebase/firestore';
import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import { ChatContext } from '../context/ChatContext';
import { db } from '../firebase';
import Saved from '../img/saved.jpeg';

const Chats = () => {
  const [chats, setChats] = useState([]);
  const [savedChatId, setSavedChatId] = useState('');

  const { currentUser } = useContext(AuthContext);
  const { dispatch } = useContext(ChatContext);

  useEffect(() => {
    const getChats = () => {
      const unsub = onSnapshot(doc(db, 'userChats', currentUser.uid), (doc) => {
        setChats(doc.data());
      });

      return () => {
        unsub();
      };
    };

    currentUser.uid && getChats();
  }, [currentUser.uid]);

  useEffect(() => {
    const getSavedChatId = async () => {
      const q = query(
        collection(db, 'userChats', currentUser.uid, 'chats'),
        orderBy('date', 'desc'),
        limit(1),
      );
      const querySnapshot = await onSnapshot(q, (querySnapshot) => {
        const chatId = querySnapshot.docs[0]?.id;
        if (chatId && chatId !== savedChatId) {
          setSavedChatId(chatId);
        }
      });

      return () => {
        querySnapshot();
      };
    };

    currentUser.uid && getSavedChatId();
  }, [currentUser.uid, savedChatId]);

  const handleSelect = (u) => {
    dispatch({ type: 'CHANGE_USER', payload: u });
  };

  const handleSaveMessage = async (text) => {
    if (!savedChatId) {
      // create saved chat
      const chatRef = await addDoc(collection(db, 'chats'), {
        users: [currentUser.uid],
        messages: [],
      });
      const chatId = chatRef.id;

      // add saved chat to user's chats
      await setDoc(doc(db, 'userChats', currentUser.uid, 'chats', chatId), {
        userInfo: {
          displayName: 'Saved',
          photoURL: Saved,
          uid: 'saved',
        },
        date: new Date(),
        lastMessage: null,
      });

      setSavedChatId(chatId);
    }

    // add message to saved chat
    await setDoc(doc(db, 'chats', savedChatId, 'messages', new Date().toISOString()), {
      text,
      sender: currentUser.uid,
      date: new Date(),
    });
  };

  return (
    <div className="chats">
      <div
        className="userChat"
        onClick={() => handleSelect({ displayName: 'Saved', photoURL: Saved, uid: 'saved' })}>
        <img src={Saved} alt="" />
        <div className="userChatInfo">
          <span>Saved</span>
          <p></p>
        </div>
      </div>
      {Object.entries(chats)
        ?.sort((a, b) => b[1].date - a[1].date)
        .map((chat) => (
          <div
            className="userChat"
            key={chat[0]}
            onClick={() =>
              handleSelect({
                displayName: chat[1].userInfo.displayName,
                photoURL: chat[1].userInfo.photoURL,
                uid: chat[0],
              })
            }>
            <img src={chat[1].userInfo.photoURL} alt="" />
            <div className="userChatInfo">
              <span>{chat[1].userInfo.displayName}</span>
              {chat[1].lastMessage && <p>{chat[1].lastMessage.text}</p>}
            </div>
          </div>
        ))}
      <div className="saveMessage">
        <input
          type="text"
          placeholder="Type a message"
          onKeyDown={(e) => e.key === 'Enter' && handleSaveMessage(e.target.value)}
        />
        <button
          onClick={() => handleSaveMessage(document.querySelector('.saveMessage input').value)}>
          Save
        </button>
      </div>
    </div>
  );
};

export default Chats;
