import React, { useContext, useEffect, useState } from 'react';
import { doc, onSnapshot, setDoc } from 'firebase/firestore';
import { AuthContext } from '../context/AuthContext';
import { ChatContext } from '../context/ChatContext';
import { db } from '../firebase';
import Saved from '../img/saved.jpeg';
import StatusUpdater from './StatusUpdater'; // Імпортуйте компонент
import UserStatus from './UserStatus';
import { auth } from '../firebase';

const Chats = () => {
  const [chats, setChats] = useState([]);

  const { currentUser } = useContext(AuthContext);
  const { dispatch } = useContext(ChatContext);

  useEffect(() => {
    const getChats = () => {
      const unsub = onSnapshot(doc(db, 'userChats', currentUser.uid), (doc) => {
        setChats(doc.data());
      });

      return () => {
        unsub();
      };
    };

    currentUser.uid && getChats();
  }, [currentUser.uid]);

  const handleSelect = (u) => {
    dispatch({ type: 'CHANGE_USER', payload: u });
  };

  const handleCreateChat = async () => {
    const chatId = `${currentUser.uid}-${currentUser.uid}`;

    await setDoc(doc(db, 'chats', chatId), {
      messages: [],
      users: [currentUser.uid],
    });

    await setDoc(doc(db, 'userChats', currentUser.uid, 'chats', chatId), {
      userInfo: {
        displayName: currentUser.displayName,
        photoURL: currentUser.photoURL,
        uid: currentUser.uid,
      },
      lastMessage: null,
      status: 'online', // Додайте поле статусу
    });

    setChats((prevChats) => ({
      ...prevChats,
      [chatId]: {
        userInfo: {
          displayName: 'Saved',
          photoURL: currentUser.photoURL,
          uid: currentUser.uid,
        },
        lastMessage: null,
        status: 'offline', // Додайте поле статусу
      },
    }));
  };

  useEffect(() => {
    handleCreateChat();
  }, [currentUser.uid]);

  return (
    <div className="chats">
      <StatusUpdater /> {/* Вставте компонент статусу */}
      <div className="userChat">
        <img src={Saved} alt="" />
        <div className="userChatInfo">
          <span onClick={() => handleCreateChat()}>Saved</span>
          <p></p>
        </div>
      </div>
      <div className="chats-list">
        {Object.entries(chats).length > 0 &&
          Object.entries(chats)
            .sort((a, b) => b[1].date - a[1].date)
            .map((chat) => (
              <div
                className="userChat"
                key={chat[0]}
                onClick={() => handleSelect(chat[1].userInfo)}>
                <img src={chat[1].userInfo.photoURL} alt="" />
                <div className="userChatInfo">
                  <span>{chat[1].userInfo.displayName}</span>
                  <p>{chat[1].lastMessage?.text}</p>
                  <UserStatus user={chat[1].userInfo} /> {/* Вставте компонент статусу */}
                </div>
              </div>
            ))}
      </div>
    </div>
  );
};

export default Chats;
