import { addDoc, collection, serverTimestamp } from 'firebase/firestore';
import { db } from '../firebase';
import { useContext } from 'react';
import Cam from '../img/cam.png';
import Add from '../img/add.png';
import More from '../img/more.png';
import Messages from './Messages';
import Input from './Input';
import { ChatContext } from '../context/ChatContext';

const sendSelfMessage = async (currentUser, messageText) => {
  // Отримуємо посилання на колекцію повідомлень чату зі собою
  const selfChatMessagesRef = collection(db, 'userChats', currentUser.uid, 'messages');

  // Додаємо нове повідомлення у колекцію повідомлень
  await addDoc(selfChatMessagesRef, {
    text: messageText,
    createdAt: serverTimestamp(),
    sender: currentUser.uid,
  });
};

const Chat = () => {
  const { data } = useContext(ChatContext);
  return (
    <div className="chat">
      <div className="chatInfo">
        <span>{data.user?.displayName}</span>
        <div className="chatIcons">
          <img src={Cam} alt="" />
          <img src={Add} alt="" />
          <img src={More} alt="" />
        </div>
      </div>
      <Messages />
      <Input />
    </div>
  );
};

export default Chat;
